binding.script:
var icao = getprop("autopilot/route-manager/departure/airport");
var apt = airportinfo(icao);
if (apt != nil)
{
	var dialog = gui.Widget.new();
	var title = "cdu-" ~ icao ~ "-rwy-select";
	dialog.set("name", title);
	dialog.set("layout", "vbox");
	var titlebar = dialog.addChild("group");
	titlebar.set("layout", "hbox");
	titlebar.addChild("empty").set("stretch", 1);
	titlebar.addChild("text").set("label", "Select departure runway for " ~ icao);
	titlebar.addChild("empty").set("stretch", 1);
	var w = titlebar.addChild("button");
	w.set("pref-width", 16);
	w.set("pref-height", 16);
	w.set("legend", "");
	w.set("default", 0);
	w.set("key", "esc");
	w.setBinding("dialog-close");
	dialog.addChild("hrule");
	var group = dialog.addChild("group");
	group.set("layout", "hbox");
	foreach (var rwy; keys(apt.runways))
	{
		var button = group.addChild("button");
		button.set("legend", rwy);
		button.setBinding("nasal", "setprop(\"autopilot/route-manager/departure/runway\", \"" ~ rwy ~ "\");");
		button.setBinding("dialog-close");
	}
	fgcommand("dialog-new", dialog.prop());
	fgcommand("dialog-show", props.Node.new({ "dialog-name": title }));
}

line.nasal:
var scroll_pos = CRJ700.cdu1.scroll_pos;
var i = scroll_pos + 0;
return getprop("autopilot/route-manager/route/wp[" ~ i ~ "]/id");

binding.script:
var scroll_pos = CRJ700.cdu1.scroll_pos;
var i = scroll_pos + 0;
setprop("instrumentation/fms/selected-waypoint", i);
CRJ700.cdu1.go_to_page("legs-2");

line.nasal:
var scroll_pos = CRJ700.cdu1.scroll_pos;
var i = scroll_pos + 0;
if (!getprop("autopilot/route-manager/route/wp[" ~ (i + 1) ~ "]/id"))
{
	return "";
}
var bearing = sprintf("%3.f", getprop("autopilot/route-manager/route/wp[" ~ i ~ "]/leg-bearing-true-deg"));
var distance = sprintf("%5.fNM", getprop("autopilot/route-manager/route/wp[" ~ i ~ "]/leg-distance-nm"));
return bearing ~ "    " ~ distance;

