page.nasal:
var i = getprop("instrumentation/fms/selected-waypoint");
var alt = getprop("autopilot/route-manager/route/wp[" ~ i ~ "]/altitude-ft");
var altN = props.globals.getNode("instrumentation/fms/waypoint-altitude-ft");
if (alt != nil)
{
	altN.setDoubleValue(alt < 0 ? 0 : alt);
}
else
{
	altN.setDoubleValue(0);
}

binding.script:
var insertN = props.globals.getNode("instrumentation/fms/insert-waypoint-id");
interactive_cdu.RouteManager.insert(getprop("instrumentation/fms/selected-waypoint"), insertN.getValue());
insertN.setValue("-ENTER ID-");
CRJ700.cdu1.go_to_page("legs-1");

var swapN = props.globals.getNode("instrumentation/fms/swap-waypoint-id");
interactive_cdu.RouteManager.swap_waypoints(getprop("instrumentation/fms/selected-waypoint"), swapN.getValue() - 1);
swapN.setValue("-ENTER NO.-");
CRJ700.cdu1.go_to_page("legs-1");

var i = getprop("instrumentation/fms/selected-waypoint");
var altN = props.globals.getNode("instrumentation/fms/waypoint-altitude-ft");
interactive_cdu.RouteManager.set_waypoint_altitude(i, altN.getValue());
CRJ700.cdu1.go_to_page("legs-1");
